.mode columns
.headers on
PRAGMA foreign_keys=ON;
 
drop table if exists FBUSER;
drop table if exists BEACH;
drop table if exists STATUS;

CREATE TABLE FBUSER (
    idUser INTEGER PRIMARY KEY,
	idFacebook TEXT NOT NULL UNIQUE,
    name VARCHAR NOT NULL,
    email TEXT UNIQUE NOT NULL
);

CREATE TABLE BEACH (
    idBeach INTEGER PRIMARY KEY,
    name TEXT NOT NULL,
    latitude DOUBLE NOT NULL,
	longitude DOUBLE NOT NULL,
	place TEXT,
	picture TEXT,
	parking	BOOLEAN DEFAULT 'false',
	blueFlag BOOLEAN DEFAULT 'false',
	restaurant BOOLEAN DEFAULT 'false',
	umbrella BOOLEAN DEFAULT 'false'
);

CREATE TABLE STATUS (
	idStatus INTEGER PRIMARY KEY,
    idUser TEXT REFERENCES FBUSER (idFacebook) ON DELETE CASCADE,
    idBeach INTEGER REFERENCES BEACH (idBeach),
	feeling INT NOT NULL,
	flag INT NOT NULL DEFAULT -1,
	sun BOOLEAN NOT NULL DEFAULT 'false',
	wind BOOLEAN NOT NULL DEFAULT 'false',
	clouds BOOLEAN NOT NULL DEFAULT 'false',
	rain BOOLEAN NOT NULL DEFAULT 'false',
	date timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP
);

